package swax.web.controller;
import java.io.IOException;import java.text.ParseException;import java.util.List;import javax.servlet.http.HttpServletRequest;import javax.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.multipart.MultipartFile;import org.springframework.web.servlet.ModelAndView;import swax.web.form.PojoModelImportCollectionForm;import swax.web.form.PojoModelRegisterForm;import swax.webservice.dto.AlbumDTO;import swax.webservice.entity.AlbumDiscogs;import swax.webservice.entity.User;import swax.webservice.service.IAlbumCollectedService;import swax.webservice.service.IAlbumDiscogsService;import swax.webservice.service.IAlbumService;import swax.webservice.service.IUserService;
@Controllerpublic class UserProfileController {
	@Autowired	private IUserService userService = null;		@Autowired	private IAlbumCollectedService albumCollectedService = null;		@Autowired	private IAlbumService albumService = null;		@Autowired	IAlbumDiscogsService albumDiscogsService = null;
	@RequestMapping(value="/loadUserProfilePage", method = RequestMethod.GET)	public ModelAndView changeUserProfile(@Valid @ModelAttribute(value="registerModelAttribute") 		final PojoModelRegisterForm registerForm, 		final BindingResult pBindingResult, HttpServletRequest request, final ModelMap pModel) {				ModelAndView mav = new ModelAndView();		User user = (User) request.getSession().getAttribute("user");				registerForm.setEmail(user.getEmail());		registerForm.setUserName(user.getUserName());		registerForm.setCountry(user.getCountry());				mav.getModel().put("registerModelAttribute", registerForm);		mav.setViewName("user/changeProfile");				return mav;	}		@RequestMapping(value="/changeProfileAction", method = RequestMethod.POST)	public ModelAndView changeProfileAction(@Valid @ModelAttribute(value="registerModelAttribute") 		final PojoModelRegisterForm registerForm, 		final BindingResult pBindingResult, HttpServletRequest request, final ModelAndView mav) {				String infoMsg="";		boolean hasCollection = false;		User user = (User) request.getSession().getAttribute("user");				user.setUserName(registerForm.getUserName());		user.setEmail(registerForm.getEmail());		user.setPassword(registerForm.getPassword());		user.setCountry(registerForm.getCountry());		user.setDiscogsName(registerForm.getDiscogsName());				userService.createUpdateEntity(user);				request.getSession().setAttribute("user", user);				List<AlbumDTO> albumsDTO = user.getAlbumsDTO();				if (albumsDTO.size() == 0) {			infoMsg = "It seems that you don't have any collection.";			mav.getModel().put("importCollectionModelAttribute", new PojoModelImportCollectionForm());		} else {			infoMsg = "You can now browse your collection and start to swap your wax ;-)";			hasCollection = true;			mav.getModel().put("userCollection", albumsDTO);		}		        mav.getModel().put("user", user);		mav.getModel().put("registerModelAttribute", registerForm);		mav.getModel().put("infoMsg", infoMsg);		mav.getModel().put("hasCollection", hasCollection);		mav.setViewName("mySwax");				return mav;	}		@RequestMapping(value="/backToMySwax")	public ModelAndView backToMySwax(HttpServletRequest request, final ModelAndView mav) {				String infoMsg="";		boolean hasCollection = false;				User user = (User) request.getSession().getAttribute("user");				List<AlbumDTO> albumsDTO = user.getAlbumsDTO();				if (albumsDTO.size() == 0) {			infoMsg = "It seems that you don't have any collection.";			mav.getModel().put("importCollectionModelAttribute", new PojoModelImportCollectionForm());		} else {			infoMsg = "You can now browse your collection and start to swap your wax ;-)";			hasCollection = true;			mav.getModel().put("userCollection", albumsDTO);		}        mav.getModel().put("user", user);		mav.getModel().put("infoMsg", infoMsg);		mav.getModel().put("hasCollection", hasCollection);		mav.setViewName("mySwax");				return mav;	}		@RequestMapping(value="/importDiscogsCollection")	public ModelAndView importDiscogsCollection(@ModelAttribute(value="importCollectionModelAttribute") 			final PojoModelImportCollectionForm importCollectionForm, HttpServletRequest request, 			final ModelAndView mav) throws IOException, ParseException {				boolean hasCollection = true;		User user = (User) request.getSession().getAttribute("user");						/** START Import with UPLOAD **/				MultipartFile file = importCollectionForm.getDiscogsFilePath();		List<AlbumDiscogs> albumsDiscogs = userService.getDiscogsCollectionUpload(file, request);				/** END Import with UPLOAD **/				albumsDiscogs = albumDiscogsService.trimAlbumsDiscogs(albumsDiscogs);		albumService.updateAlbumTable(albumsDiscogs);		albumCollectedService.createUserCollection(user, albumsDiscogs);				List<AlbumDTO> albumsDTO = user.getAlbumsDTO();				System.out.println("ALBUMS = "+albumsDTO.size());				if (albumsDTO.size()==0) {			hasCollection = false;		}				request.getSession().setAttribute("hasCollection", hasCollection);		mav.getModel().put("userCollection", albumsDTO);		mav.setViewName("mySwax");				return mav;	}
}