package swax.web.controller;
import javax.servlet.http.HttpServletRequest;import javax.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.servlet.ModelAndView;import swax.web.form.PojoModelRegisterForm;import swax.webservice.entity.AlbumCollected;import swax.webservice.entity.User;import swax.webservice.service.IUserService;
@Controllerpublic class UserProfileController {
	@Autowired	private IUserService userService = null;
	@RequestMapping(value="/loadUserProfilePage", method = RequestMethod.GET)	public ModelAndView changeUserProfile(@Valid @ModelAttribute(value="registerModelAttribute") 		final PojoModelRegisterForm registerForm, 		final BindingResult pBindingResult, HttpServletRequest request, final ModelMap pModel) {				ModelAndView mav = new ModelAndView();		User user = (User) request.getSession().getAttribute("user");				registerForm.setEmail(user.getEmail());		registerForm.setUserName(user.getUserName());		registerForm.setCountry(user.getCountry());				mav.getModel().put("registerModelAttribute", registerForm);		mav.setViewName("user/changeProfile");				return mav;	}		@RequestMapping(value="/changeProfileAction", method = RequestMethod.POST)	public ModelAndView changeProfileAction(@Valid @ModelAttribute(value="registerModelAttribute") 		final PojoModelRegisterForm registerForm, 		final BindingResult pBindingResult, HttpServletRequest request, final ModelAndView mav) {				User user = (User) request.getSession().getAttribute("user");				user.setUserName(registerForm.getUserName());		user.setEmail(registerForm.getEmail());		user.setPassword(registerForm.getPassword());		user.setCountry(registerForm.getCountry());				userService.createUpdateEntity(user);				request.getSession().setAttribute("user", user);				mav.getModel().put("userCollection", user.getCollection());		for (AlbumCollected album: user.getCollection()) {			System.out.println(album.getAlbum().getAlbumName());			}				mav.getModel().put("registerModelAttribute", registerForm);		mav.setViewName("mySwax");				return mav;	}		@RequestMapping(value="/backToMySwax")	public ModelAndView backToMySwax(final ModelAndView mav) {		mav.setViewName("mySwax");				return mav;	}
}