package swax.web.controller.user;
import java.io.IOException;import java.text.ParseException;import java.util.List;import java.util.Map;import javax.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.multipart.MultipartFile;import org.springframework.web.servlet.ModelAndView;import swax.web.form.PojoModelImportCollectionForm;import swax.web.form.PojoModelImportWantlistForm;import swax.webservice.entity.album.AlbumDiscogsWantlist;import swax.webservice.entity.user.User;import swax.webservice.service.album.IAlbumDiscogsService;import swax.webservice.service.album.IAlbumDiscogsWantlistService;import swax.webservice.service.album.IAlbumService;import swax.webservice.service.album.IAlbumWantlistService;import swax.webservice.service.user.IUserService;
@Controllerpublic class ImportWantlistController {
	@Autowired	private IUserService userService = null;		@Autowired	private IAlbumWantlistService albumWantlistService = null;		@Autowired	private IAlbumService albumService = null;		@Autowired	IAlbumDiscogsService albumDiscogsService = null;		@Autowired	IAlbumDiscogsWantlistService albumDiscogsWantlistService = null;		@RequestMapping(value="/importDiscogsWantlist")	public ModelAndView importDiscogsWantlist(@ModelAttribute(value="importWantlistModelAttribute") 			final PojoModelImportWantlistForm importWantlistForm, HttpServletRequest request, 			final ModelAndView mav) throws IOException, ParseException {				User user = (User) request.getSession().getAttribute("user");						/** START Import with UPLOAD **/				MultipartFile file = importWantlistForm.getWantlistFilePath();		List<AlbumDiscogsWantlist> albumsDiscogsWantlist = userService.getDiscogsWantlistUpload(file, request);				/** END Import with UPLOAD **/				albumsDiscogsWantlist = albumDiscogsWantlistService.trimAlbumsDiscogsWantlist(albumsDiscogsWantlist);		albumService.updateAlbumTableWithWantlist(albumsDiscogsWantlist);		albumWantlistService.createUserWantlist(user, albumsDiscogsWantlist);				Map<String, Object> mapInitUser = userService.initUser(user);				mav.getModel().put("hasCollection", mapInitUser.get("hasCollection"));		mav.getModel().put("userCollection", mapInitUser.get("userCollection"));		mav.getModel().put("hasWantlist", mapInitUser.get("hasWantlist"));		mav.getModel().put("userWantlist", mapInitUser.get("userWantlist"));		mav.getModel().put("latestAdditions", mapInitUser.get("latestAdditions"));		if (!(boolean) mapInitUser.get("hasCollection")) {			mav.getModel().put("importCollectionModelAttribute", new PojoModelImportCollectionForm());		}		mav.setViewName("user/mySwax");				return mav;	}
}