package swax.web.controller.user;
import org.apache.log4j.Logger;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;import swax.web.form.PojoModelSelectNotificationsForm;/** *  * @author Matthieu Ibarra * Controller for: * /deleteNotifications * */
@Controllerpublic class NotificationsController {		private Logger logger = Logger.getLogger(this.getClass());		@RequestMapping(value="/deleteNotifications")	public ModelAndView deleteNotifications(ModelAndView mav, @ModelAttribute(value="selectNotificationsModelAttribute") PojoModelSelectNotificationsForm selectNotificationsForm) {				logger.debug(this.getClass().getName()+": deleteNotifications");						String[] notificationsToDelete = selectNotificationsForm.getNotificationsSelected();				for (String selectedId: notificationsToDelete) {			System.out.println("Id sélectionné: "+selectedId);		}				mav.setViewName("user/mySwax");						return mav;			}	}