package swax.web.controller.user;
import java.util.Map;import javax.servlet.http.HttpServletRequest;import javax.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.servlet.ModelAndView;import swax.web.form.PojoModelImportCollectionForm;import swax.web.form.PojoModelImportWantlistForm;import swax.web.form.PojoModelRegisterForm;import swax.webservice.entity.user.User;import swax.webservice.service.album.IAlbumDiscogsService;import swax.webservice.service.user.IUserService;
@Controllerpublic class UserProfileController {
	@Autowired	private IUserService userService;		@Autowired	IAlbumDiscogsService albumDiscogsService;
	@RequestMapping(value="/loadUserProfilePage", method = RequestMethod.GET)	public ModelAndView changeUserProfile(@Valid @ModelAttribute(value="registerModelAttribute") 		final PojoModelRegisterForm registerForm, 		final BindingResult pBindingResult, HttpServletRequest request, final ModelMap pModel) {				ModelAndView mav = new ModelAndView();		User user = (User) request.getSession().getAttribute("user");				registerForm.setEmail(user.getEmail());		registerForm.setUserName(user.getUserName());		registerForm.setCountry(user.getCountry());				mav.getModel().put("registerModelAttribute", registerForm);		mav.setViewName("user/changeProfile");				return mav;	}		@RequestMapping(value="/changeProfileAction", method = RequestMethod.POST)	public ModelAndView changeProfileAction(@Valid @ModelAttribute(value="registerModelAttribute") 		final PojoModelRegisterForm registerForm, 		final BindingResult pBindingResult, HttpServletRequest request, ModelAndView mav) {				User user = (User) request.getSession().getAttribute("user");				user.setUserName(registerForm.getUserName());		user.setEmail(registerForm.getEmail());		user.setPassword(registerForm.getPassword());		user.setCountry(registerForm.getCountry());		user.setDiscogsName(registerForm.getDiscogsName());				userService.createUpdateEntity(user);				return mav = this.mySwax(user);	}		@RequestMapping(value="/backToMySwax")	public ModelAndView backToMySwax(HttpServletRequest request, ModelAndView mav) {		User user = (User) request.getSession().getAttribute("user");		return mav = this.mySwax(user);	}		public ModelAndView mySwax(User user) {				ModelAndView mav = new ModelAndView();		Map<String, Object> mapInitUser = userService.initUser(user);				mav.getModel().put("hasCollection", mapInitUser.get("hasCollection"));		mav.getModel().put("userCollection", mapInitUser.get("userCollection"));		mav.getModel().put("hasWantlist", mapInitUser.get("hasWantlist"));		mav.getModel().put("userWantlist", mapInitUser.get("userWantlist"));		mav.getModel().put("latestAdditions", mapInitUser.get("latestAdditions"));		if (!(boolean) mapInitUser.get("hasCollection")) {			mav.getModel().put("importCollectionModelAttribute", new PojoModelImportCollectionForm());		}		if (!(boolean) mapInitUser.get("hasWantlist")) {			mav.getModel().put("importWantlistModelAttribute", new PojoModelImportWantlistForm());				}				mav.setViewName("user/mySwax");				return mav;			}
}