package swax.web.form;
import org.springframework.web.multipart.MultipartFile;
public class PojoModelSwapAlbumForm {		private String mediaGrading;		private String sleeveGrading;		private String waxValue;		private String description;		private MultipartFile imgFilePath;		private Integer albumId;	private String errorMsg = "";	public String getMediaGrading() {		return mediaGrading;	}	public void setMediaGrading(String mediaGrading) {		this.mediaGrading = mediaGrading;	}	public String getSleeveGrading() {		return sleeveGrading;	}	public void setSleeveGrading(String sleeveGrading) {		this.sleeveGrading = sleeveGrading;	}	public String getWaxValue() {		return waxValue;	}	public void setWaxValue(String waxValue) {		this.waxValue = waxValue;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public MultipartFile getImgFilePath() {		return imgFilePath;	}	public void setImgFilePath(MultipartFile imgFilePath) {		this.imgFilePath = imgFilePath;	}	public String getErrorMsg() {		return errorMsg;	}	public void setErrorMsg(String errorMsg) {		this.errorMsg = errorMsg;	}	public Integer getAlbumId() {		return albumId;	}	public void setAlbumId(Integer albumId) {		this.albumId = albumId;	}	}