package swax.webservice.entity.notification;
import static javax.persistence.GenerationType.IDENTITY;import java.io.Serializable;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import lombok.Data;import swax.webservice.entity.user.User;
@Entity@Table(name="NOTIFICATION")@Datapublic class Notification implements Serializable {	private static final long serialVersionUID = 7754329056261974650L;	@Id	@GeneratedValue(strategy = IDENTITY)	@Column(name="NOTIFICATION_ID", unique = true, nullable = false)	private Integer notificationId;		@ManyToOne	@JoinColumn(name="USER_ID", nullable = false)	private User user;		@Column(name="DATE_ADDED", nullable = false)	private String dateAdded;		@Column(name="TYPE", nullable = false)	private String type;		@Column(name="SUBJECT", nullable = false)	private String subject;		@Column(name="STATUS", nullable = false)	private String status;		@Column(name="MESSAGE", nullable = false, columnDefinition="TEXT")	private String message;		/** CONSTRUCTORS **/	public Notification(User user, String dateAdded, String type, String subject, String status, String message) {		this.user = user;		this.dateAdded = dateAdded;		this.type = type;		this.subject = subject;		this.message = message;		this.status = status;	}	public Notification() {	}	}