package swax.webservice.entity.user;
import static javax.persistence.GenerationType.IDENTITY;import java.io.Serializable;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import lombok.Data;
@Entity@Table(name="NOTIFICATION")@Datapublic class Notification implements Serializable {	private static final long serialVersionUID = 7754329056261974650L;	@Id	@GeneratedValue(strategy = IDENTITY)	@Column(name="NOTIFICATION_ID", unique = true, nullable = false)	private Integer notificationId;		@ManyToOne	@JoinColumn(name="USER_ID", nullable = false)	private User user;		private String dateAdded;		private String subject;		private String message;		private String status;		private String details;		/** CONSTRUCTORS **/	public Notification(User user, String dateAdded, String subject, String message, String status, String details) {		this.user = user;		this.dateAdded = dateAdded;		this.subject = subject;		this.message = message;		this.status = status;		this.details = details;	}	public Notification() {	}	}