/* USER TABLE INIT */
DROP TABLE IF EXISTS USER;

CREATE TABLE USER(USER_ID  INT AUTO_INCREMENT PRIMARY KEY, EMAIL VARCHAR(255) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, 
	USER_NAME VARCHAR(255) NOT NULL, DISCOGS_NAME VARCHAR(255), COUNTRY VARCHAR(255), DATE_SIGNUP TIMESTAMP, 
	LAST_CONNEXION TIMESTAMP, STATUS VARCHAR(255));

SELECT * FROM USER ORDER BY USER_ID;

/* WANTLIST TABLE INIT */

DROP TABLE IF EXISTS SWAPALBUM;

CREATE TABLE SWAPALBUM(SWAP_ALBUM_ID INT AUTO_INCREMENT PRIMARY KEY, USER_ID INT NOT NULL, 
	ALBUM_COLLECTED_ID VARCHAR(255) NOT NULL, MEDIA_GRADING VARCHAR(255), SLEEVE_GRADING VARCHAR(255), 
	WAX_VALUE VARCHAR(255), DESCRIPTION VARCHAR(255), IMG_NAME VARCHAR(255), DATE_ADDED VARCHAR(255));

/* WANTLIST TABLE INIT */

DROP TABLE IF EXISTS WANTLIST;

CREATE TABLE WANTLIST(ALBUM_WANTLIST_ID INT AUTO_INCREMENT PRIMARY KEY, USER_ID INT NOT NULL, 
	DISCOGS_ID VARCHAR(255) NOT NULL, NOTES VARCHAR(255), DATE_ADDED VARCHAR(255));

/* ALBUM_COLLECTED TABLE INIT */
DROP TABLE IF EXISTS COLLECTION;

CREATE TABLE COLLECTION(ALBUM_COLLECTED_ID INT AUTO_INCREMENT PRIMARY KEY, USER_ID INT, DISCOGS_ID VARCHAR(255), MEDIA_GRADING VARCHAR(255),
	SLEEVE_GRADING VARCHAR(255), NOTES VARCHAR(255), DATE_ADDED VARCHAR(255));

/* ALBUM TABLE INIT */
DROP TABLE IF EXISTS ALBUM;
	
CREATE TABLE ALBUM(DISCOGS_ID VARCHAR(255) PRIMARY KEY, ALBUM_NAME VARCHAR(255), ARTIST VARCHAR(255),
	LABEL VARCHAR(255), CATALOG_NUMBER VARCHAR(255), FORMAT VARCHAR(255), RELEASE_DATE VARCHAR(255));
	
	
